// import './App.css';

// import { type } from "@testing-library/user-event/dist/type";
import Alert from "./components/Alert";
import NavBar from "./components/NavBar";
import TextForm2 from "./components/TextForm2";
import { useState } from "react";


function App() {
  const [mode, setMode] = useState("light");
  const [textColor, setTextColor] = useState("dark")
  const[style, setStyle] = useState({
    color : "black",
    backgroundColor : "white"
  })
  const toogleThemeBtn = ()=>{
    if (mode === "light") {
      setMode("dark");
      setTextColor("light");
      setStyle({
        color : "white",
        backgroundColor : "#010627"
      })
      setAlert("Dark mode has Enabled","Success")
    }
    else{
      setMode("light");
      setTextColor("dark");
      setStyle({
        color : "black",
        backgroundColor : "white"
      })
      setAlert("Light mode has Enabled","Success")

    }
  }
// alert 
const [alert, setAlert] = useState("showAlert")

const showAlert = (message,type)=>{
  setAlert({
    msg : message,
    typ : type
  })
}


  return (
    <>
      <NavBar title="TextUtiles" mode={mode} textColor={textColor} toogleThemeBtn={toogleThemeBtn}/>
      <Alert alert={alert}/>
      <TextForm2 heading="Enter Any Charecter" style={style}/>
    </>
  );
}

export default App;
