// import './App.css';

import NavBar from "./components/NavBar";
import TextForm2 from "./components/TextForm2";
import { useState } from "react";


function App() {
  const [mode, setMode] = useState("light");

  const[style, setStyle] = useState({
    color : "black",
    backgroundColor : "white"
  })
  const toogleThemeSwitch = ()=>{
    if(mode === 'light'){
      setMode("Dark");
      setStyle({
        color : "white",
        backgroundColor : "black"
      })
    }
  }
  return (
    <>
      <NavBar title="TextUtiles" mode={mode} toogleThemeSwitch={toogleThemeSwitch}/>
      <TextForm2 heading="Enter Any Charecter" style={style}/>
    </>
  );
}

export default App;
