// import './App.css';

// import { type } from "@testing-library/user-event/dist/type";
import Alert from "./components/Alert";
import NavBar from "./components/NavBar";
import TextForm2 from "./components/TextForm2";
import { useState } from "react";


function App() {
  const [mode, setMode] = useState("light");
  const [textColor, setTextColor] = useState("dark") //edi button text kosam 
  const[style, setStyle] = useState({
    color : "black",
    backgroundColor : "white"
  })
  const toogleThemeBtn = ()=>{
    if (mode === "light" || mode === "success") {
      setMode("dark");
      setTextColor("light");
      setStyle({
        color : "white",
        backgroundColor : "#010627"
      })
      showAlert("Dark mode has Enabled","success")
    }
    else{
      setMode("light");
      setTextColor("dark");
      setStyle({
        color : "black",
        backgroundColor : "white"
      })
      showAlert("Light mode has Enabled","success")

    }
  }
//  mode blue

const [success , setSuccess] = useState('primary')
const successBtn = ()=>{
  if(mode === "light" || mode === "dark"){
    setSuccess("light")
    setMode("success");
    setStyle({
      color : "white",
      backgroundColor: '#4CAF50'
    })
    showAlert("Green mode has Enabled","success")
  }
  else{
    setSuccess("success")
    setMode("light");
    setStyle({
      color : "black",
      backgroundColor: 'white'
    })
    showAlert("Light mode has Enabled","success")
  }
}





// alert 
const [alert, setAlert] = useState(null)

const showAlert = (message,type)=>{
  setAlert({
    msg : message,
    typ : type
  })
  setTimeout(() => {
    setAlert(null)
  }, 1200);
}


  return (
    <>
      <NavBar title="TextUtiles" mode={mode} textColor={textColor} toogleThemeBtn={toogleThemeBtn} success={success} successBtn={successBtn}/>
      <Alert alert={alert}/>
      <TextForm2 showAlert={showAlert} heading="Enter Any Charecter" style={style}/>
    </>
  );
}

export default App;
